$("[data-categories]").each(function(){$(this).sortable({placeholder:"ui-state-highlight",handle:".handle",update:function(e,t){fetch("/categories/sort",{method:"PUT",headers:{"Content-Type":"application/json"},body:JSON.stringify({categories:Array.from(t.item.closest("ul").children("li")).map(e=>e.dataset.id)})})}}),$(this).disableSelection()});