function orderCategories(){var e=document.getElementById("categories-list").querySelectorAll("li");fetch("/categories/sort",{method:"PUT",headers:{"Content-Type":"application/json"},body:JSON.stringify({categories:Array.from(e).map(e=>e.dataset.id)})})}$("#categories-list").sortable({placeholder:"ui-state-highlight",update:()=>{orderCategories()}}),$("#categories-list").disableSelection(),$('[data-sort="up"]').each(function(){$(this).on("click",function(){var e=$(this).closest("li"),t=e.prev();t&&(e.insertBefore(t),orderCategories())})}),$('[data-sort="down"]').each(function(){$(this).on("click",function(){var e=$(this).closest("li"),t=e.next();t&&(e.insertAfter(t),orderCategories())})});